package javaPackage1;

public class AssigmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int i=10;
	
		i*=i;   //do not add space between * and =
	System.out.println(i);
	i/=i;  // do not add space between / and =
	System.out.println(i);
	i+=i;  //do not add space between + and =
	System.out.println(i);
	i-=i; //do not add space between - and =
	System.out.println(i);
	}

}
